import com.santaba.agent.debugger.TlistTask

/*
 Script to discover tasks being run against the resource.
 Creates a taskTypesList property which forms the instances for the batchscript collection
  and for comparison in the 'all tasks' DataSource.
 This is because during collection, !tlist may return nothing just after collector restart.
*/

// Set debug=true to print debug outputs. Leave =false for normal operation.
debug = false;

hostname = hostProps.get('system.hostname');

try
{
	outputList = [];

	TlistTask debug = new TlistTask('!tlist h=' + hostname + ' summary=true');

	debug.run()
	debug.awaitFinish()
	debugOutput = debug.output

	debugPrinter(debugOutput);

	regexMatcher = /^\s*(\S+)\s+(\S+)\s+(\d+)/;
	debugOutput.eachLine()
	{ line ->
		if(line =~ regexMatcher)
		{
			sourceType = (line =~ regexMatcher)[0][1];
			sourceCollector = (line =~ regexMatcher)[0][2];

			// There is no point including EventSources here, because !tlist doesn't tell you if
			//  they're working or not anyway (only !tdetail gives this level of detail and we aren't
			//  about to do that for all of however many there might be)
			// There is also no point splitting ConfigSources and DataSources, as the !tlist used in the
			//  detailed collection method doesn't indicate any difference.
			if(sourceType != 'EventSource')
			{
				outputList << sourceCollector;			
			}
		}
	}

	debugPrinter(outputList);

	println 'auto.taskTypesList.v2=' + outputList.join(',')
}
catch (e)
{
	// Something went wrong... Return non-zero to avoid removing existing properties
	debugPrinter(e)
	return 1;
}

return 0;

// ---------------------------------------------------------------------------------------
// End core logic. Helper functions below.
// ---------------------------------------------------------------------------------------


// ---------------------------------------------------------------------------------------
// Function to print debug outputs.
def debugPrinter(text)
{
	// Just prints debug text, if enabled.
	if(debug)
	{
		println 'DEBUG: ' + text;
	}
}
